// Importez le module mysql
import mysql from 'mysql';

// Configuration de la connexion à la base de données
const dbConfig = {
  host: 'localhost', // Adresse de la base de données
  user: 'root',      // Nom d'utilisateur
  password: '',  // Mot de passe
  database: 'users'   // Nom de la base de données
};

// Création de la connexion à la base de données
const connection = mysql.createConnection(dbConfig);

// Connexion à la base de données
connection.connect((err) => {
  if (err) {
    console.error('Erreur de connexion à la base de données : ' + err.stack);
    // Afficher une alerte avec le message d'erreur
    alert('Erreur de connexion à la base de données : ' + err.message);
    return;
  }
  console.log('Connexion à la base de données établie avec l\'ID ' + connection.threadId);
});

// Exportez la connexion pour l'utiliser dans d'autres fichiers
export default connection;
