// src/pages/PageEnrolement.jsx
// Position: \src\Pages\PageEnrolement.jsx

import React, { useState }  from 'react';
import Boutton from '../Components/Boutton';
import Footer from '../Components/Footer';
import Titre from '../Components/Titre';
import { useNavigate } from 'react-router-dom';
import '../Componentsenrolement.css';

function PageEnrolement() {


  const [nomPrenom, setNomPrenom] = useState('');

  // Gestionnaire de changement pour l'input "Nom et prénom"
  const handleNomPrenomChange = (e) => {
    setNomPrenom(e.target.value);
  };


  const navigate = useNavigate();
  const handleBack = () => {
    // Naviguez vers la page précédente
    navigate(-1);
  };


  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [matricule, setMatricule] = useState('');
  
  // Fonction pour gérer l'enregistrement
  const handleEnregistrer = () => {
    // Vérifiez si tous les champs obligatoires sont remplis
    if (
      document.getElementById('nomPrenomInput').value &&
      document.getElementById('zoneInput').value &&
      document.getElementById('secteurInput').value &&
      document.getElementById('telephone1Input').value &&
      document.getElementById('montantInput').value &&
      document.getElementById('categorieInput').value
    ){
      // Si les conditions sont remplies, générer le matricule et afficher le pop-up
      const generatedMatricule = generateMatricule();
      setMatricule(generatedMatricule);
      setShowSuccessModal(true);
    } else {
      // Affichez une alerte si des champs obligatoires ne sont pas remplis
      alert("Veuillez remplir tous les champs obligatoires.");
    }
  };

  // Ajoutez cette fonction pour générer un numéro de matricule aléatoire
  const generateMatricule = () => {
    const randomNumber = Math.floor(10000000 + Math.random() * 90000000);
    return randomNumber.toString().replace(/(\d{3})(\d{3})(\d{2})/, '$1 $2 $3');
  };


  return (
    <div>
      {/* 1. Bouton Retour avec icône */}
      <div>
        <button onClick={handleBack}>
          <img src="/Boutton.png" alt="Boutton Retour" />
        </button>
      </div>

      {/* 2. Titre "Enrôler" */}
      <Titre titrePage="Enrôler" />

      {/* 3. Input "Nom et prénom *" */}
      <div>
        <label htmlFor="nomPrenomInput">Nom et prénom *</label>
        <div>
          <input type="text"
                id="nomPrenomInput"
                required 
                value = {nomPrenom}
                onChange={handleNomPrenomChange}/>
        </div>
      </div>

      {/* 4. Input "Raison social" */}
      <div>
        <label htmlFor="raisonSocialInput">Raison social</label>
        <div>
          <input type="text" id="raisonSocialInput" />
        </div>
      </div>

      {/* 5. Deux inputs "Zone *" et "Secteur *" */}
      <div>
      <div>
        <label htmlFor="zoneInput">Zone *</label>
        <div>
          <select id="zoneInput" required>
            {/* Options de la liste déroulante */}
            <option value="option1">Option 1</option>
            <option value="option2">Option 2</option>
            {/* ... Autres options ... */}
          </select>
        </div>
      </div>
      <div>
        <label htmlFor="secteurInput">Section *</label>
        <div>
          <select id="secteurInput" required>
            {/* Options de la liste déroulante */}
            <option value="option1">Option 1</option>
            <option value="option2">Option 2</option>
            {/* ... Autres options ... */}
          </select>
        </div>
      </div>
      </div>

      {/* 6. Deux inputs "Telephone 1 *" et "Telephone 2" */}
      <div>
        <div>
          <label htmlFor="telephone1Input">Telephone 1 *</label>
          <input type="tel" id="telephone1Input" required />
        </div>
        <div>
          <label htmlFor="telephone2Input">Telephone 2</label>
          <input type="tel" id="telephone2Input" />
        </div>
      </div>

      {/* 7. Input "Montant *" */}
      <div>
        <label htmlFor="montantInput">Montant *</label>
        <div>
          <input type="text" id="montantInput" required />
        </div>
      </div>

      {/* 8. Input "Categorie *" (liste déroulante) */}
      <div>
        <label htmlFor="categorieInput">Categorie *</label>
        <div>
          <select id="categorieInput" required>
            {/* Options de la liste déroulante */}
            {/* Le 1erLabel désactivé pour être le premier élément visible */}
            <option value="" disabled hidden>Choisissez une catégorie</option>
            <option value="option1">Option 1</option>
            <option value="option2">Option 2</option>
            {/* ... Autres options ... */}
          </select>
        </div>
      </div>

      {/* 9. Bouton "Enregistrer" */}
      <Boutton texte="Enregistrer" onClick={handleEnregistrer} />

      {/* Pop-up de succès */}
      {showSuccessModal && (
        <div className="success-modal">
          <button onClick={() => setShowSuccessModal(false)}>X</button>
          <h1>Enregistrement réussi</h1>
          <p>{/* Insérez ici le contenu du 1er input (nom et prénom) */} a le matricule N°</p>
          <p>{matricule}</p>
        </div>
      )}

      {/* 10. Footer */}
      <Footer />
    </div>
  );
}

export default PageEnrolement;
