// src/pages/PageEncaissement.jsx
// Position: \src\Pages\PageEncaissement.jsx
import React, { useState } from 'react';
import Titre from '../Components/Titre';
import Boutton from '../Components/Boutton';
import Footer from '../Components/Footer';
import './Encaissement.css';

function PageEncaissement() {

  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const handleCloseSuccessModal = () => { setShowSuccessModal(false); };
  const handleEncaisser = () => { setShowSuccessModal(true); };


  return (
    <div>
      {/* 1. Titre "Encaisser" */}
      <Titre titrePage="Encaisser" />

      {/* 2. Input "Matricule *" */}
      <div>
        <label htmlFor="matriculeInput">Matricule *</label>
        <div>
          <input type="text" id="matriculeInput" required />
        </div>
      </div>

      {/* 3. Appeler le composant Boutton avec le texte "Encaisser" */}
      <Boutton texte="Encaisser" onClick={handleEncaisser}/>

      {/* Pop-up de succès */}
      {showSuccessModal && (
        <div className="success-modal">
          <button onClick={handleCloseSuccessModal}className="close-button">X</button>
          <h1>Encaissement réussi</h1>
          <p>Bienvenu a payé</p>
          <h1>10.000 FCFA</h1>
          <p> et a le recu N° </p>
          <p>2 3 7 6</p>
        </div>
      )}

      {/* 4. Appeler le Footer */}
      <Footer />
    </div>
  );
}

export default PageEncaissement;
