// CreateAccount1.jsx

import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import Logo from '../Components/Logo.jsx';
import InputMdp from '../Components/InputMdp';
import BouttonContinuez from '../Components/BouttonContinuez.jsx';
import './CreateAccount1.css';

function CreateAccount1 () {
   
   const navigate = useNavigate();
   const handleBack = () => {
      // Naviguez vers la page précédente
      navigate(-1);
  };

   return (
      <div>
         <button className="back-button" onClick={handleBack}>
            <img src="/iconButtons.svg" alt="Retour" />
         </button>

         <div className="header-section">
            <h2>Créez un compte</h2>
            <img src="/BarreDEtat1.png" alt="Barre d'Etat" />
         </div>

         <Logo />

         <h2 className='Inscription-title'>Inscription</h2>

         <div className='input-container'>
            <input type="Nom" id="Nom" name="Nom" placeholder="Nom" className='input-text' />
         </div>

         <div className='input-container'>
            <input type="Prénom" id="Prénom" name="Prénom" placeholder="Prénom" className='input-text' />
         </div>

         <div className='input-container'>
            <input type="Numéro" id="Numéro" name="Numéro" placeholder="Numéro" className='input-text, num' />
         </div>

         <InputMdp />

         <div className="Accept-container">
            <input type="checkbox" id="Accept" name="Accept" />
            <label htmlFor="Accept" className="Accept-label">
               Je suis d’accord avec les Termes & Conditions d’utilisation
            </label>
         </div>

         <Link to="/create-account-2" className="botoncont"> <BouttonContinuez /> </Link>

      </div>
   )
}
export default CreateAccount1;
