// src/pages/PageEncaissement.jsx
// Position: \src\Pages\PageEncaissement.jsx
import React, { useState } from 'react';
import Titre from '../Components/Titre';
import Boutton from '../Components/Boutton';
import Footer from '../Components/Footer';
import PopupEncaisser from './PopupEncaisser';
import '../Styles/PageEncaissement.css';

function PageEncaissement() {

  const [showP, setShowP] = useState(false);
  const handleCloseP = () => {
    setShowP(false);
  };
  const handleEncaisser = () => { 
    setShowP(true); 
  };

  return (
    <div className="encaissement-container">
      {/* 1. Titre "Encaisser" */}
      <Titre titrePage="Encaisser" />

      {/* 2. Input "Matricule *" */}
      <div className="encaissement-input">
        <label htmlFor="matriculeInput">Matricule *</label>
        <div>
          <input type="text" id="matriculeInput" required />
        </div>
      </div>

      {/* 3. Appeler le composant Boutton avec le texte "Encaisser" */}
      <Boutton texte="Encaisser" onClick={handleEncaisser} className="encaissement-button" />

      {/* Pop-up de succ√®s */}
      {showP && <PopupEncaisser onClose={handleCloseP} />}

      {/* 4. Appeler le Footer */}
      <Footer />
    </div>
  );
}

export default PageEncaissement;
