import React, { useState } from 'react';
import './LoginPage.css';
import Logo from '../Components/Logo.jsx';
import InputEmail from '../Components/InputEmail';
import InputMdp from '../Components/InputMdp';
import connection from './dbConfig';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [motDePasse, setMotDePasse] = useState('');
  const [isLoggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    // Effectuez la vérification dans la base de données ici
    const query = `SELECT * FROM utilisateurs WHERE email = ? AND mot_de_passe = ?`;
    connection.query(query, [email, motDePasse], (err, results) => {
      if (err) {
        console.error('Erreur lors de la vérification des informations d\'identification : ' + err.message);
        alert('Erreur lors de la vérification des informations d\'identification : ' + err.message);
        return;
      }

      if (results.length > 0) {
        // L'utilisateur a été trouvé, vous pouvez rediriger ou effectuer d'autres actions ici
        alert('Connexion réussie !');
        // Mettez à jour l'état pour montrer que l'utilisateur est connecté
        setLoggedIn(true);
      } else {
        // Aucun utilisateur trouvé avec ces informations d'identification
        alert('Email ou mot de passe incorrect.');
      }
    });
  };

  const handleLogout = () => {
    // Effectuez les actions nécessaires pour déconnecter l'utilisateur
    // Cela peut inclure la suppression des jetons d'authentification, la suppression des cookies, etc.

    // Exemple : Alerte pour montrer que la déconnexion a réussi
    alert('Déconnexion réussie !');

    // Mettez à jour l'état pour montrer que l'utilisateur est déconnecté
    setLoggedIn(false);

    // Fermez la connexion à la base de données
    connection.end();
  };

  return (
    <div>
      <Logo />

      <h2 className='connection-title'>Connection</h2>

      <InputEmail value={email} onChange={(e) => setEmail(e.target.value)} />

      <InputMdp value={motDePasse} onChange={(e) => setMotDePasse(e.target.value)} />

      <div className="sesouv-mpo-container">
        <input type="checkbox" id="remember" name="remember" />
        <label htmlFor="remember" className="sesouv-mpo-label">
          Se souvenir
        </label>
        <a href="#" className="pdc-link">
          Mot de passe oublié?
        </a>
      </div>

      <div className="btn">
        {isLoggedIn ? (
          // Affiche le bouton de déconnexion si l'utilisateur est connecté
          <button onClick={handleLogout} className="deconnexion-btn">Déconnexion</button>
        ) : (
          // Affiche le bouton de connexion si l'utilisateur n'est pas connecté
          <button onClick={handleLogin} className="seconnecter-btn">Se connecter</button>
        )}
      </div>

      <div className="pdc">
        <p className="inscription-text">Pas de compte ? <a href="#">Inscrivez-vous!</a></p>
      </div>
    </div>
  );
}

export default LoginPage;
