// CreateAccount3.jsx

import React, { useState, useRef, useEffect } from 'react';
import Logo from '../Components/Logo.jsx';
import BouttonContinuez from '../Components/BouttonContinuez.jsx';
import './CreateAccount3.css';

function CreateAccount3 () {
   const [code, setCode] = useState(['', '', '', '', '']);
   const [activeInput, setActiveInput] = useState(0);
   const inputs = useRef([]);
 
   useEffect(() => {
     if (inputs.current[activeInput]) {
       inputs.current[activeInput].focus();
     }
   }, [activeInput]);
 
   const handleInputChange = (index, value) => {
     const newCode = [...code];
     newCode[index] = value;
 
     // Move focus to the next input
     if (index < 4 && value !== '') {
       setActiveInput(index + 1);
     }
 
     setCode(newCode);
   };
 
   const handleInputClick = (index) => {
     setActiveInput(index);
   };

   return (
      <div>
         <button className="back-button">
            <img src="/iconButtons.svg" alt="Retour" />
         </button>

         <div className="header-section">
            <h2>Créez un compte</h2>
            <img src="/BarreDEtat3.png" alt="Barre d'Etat" />
         </div>

         <Logo />

         <h3 className='verf-email'>Vérification adresse mail</h3>

         <p className = 'text'> Saisissez le code envoyez a votre mail;
            Sben***********@gmail.com </p>
         
         <div className="code-inputs">
            {Array.from({ length: 5 }, (_, index) => (
               <input
                  key={index}
                  type="text"
                  value={code[index]}
                  onChange={(e) => handleInputChange(index, e.target.value)}
                  onClick={() => handleInputClick(index)}
                  ref={(el) => (inputs.current[index] = el)}
                  className={activeInput === index ? 'active' : ''}
                  maxLength={1}
               />
            ))}
         </div>

         <BouttonContinuez />

      </div>
   )
}
export default CreateAccount3;
